import React, { useState, useEffect, useCallback } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import './TimeChallengeGame.css';

interface TimeChallengeGameProps {
  isActive: boolean;
  onGameEnd: (score: number) => void;
  onExit: () => void;
}

// ÁîüÊàêÈö®Ê©üÁöÑÂãæÂãæ‰ΩçÁΩÆ
const generateRandomTargets = () => {
  const targetCount = Math.floor(Math.random() * 4) + 6; // 6-9ÂÄãÂãæÂãæ
  const targets = [];
  const usedPositions = new Set();
  
  while (targets.length < targetCount) {
    const position = Math.floor(Math.random() * 20); // 20ÂÄã‰ΩçÁΩÆ
    if (!usedPositions.has(position)) {
      usedPositions.add(position);
      targets.push(position);
    }
  }
  
  return targets.sort((a, b) => a - b);
};

const TimeChallengeGame: React.FC<TimeChallengeGameProps> = ({ isActive, onGameEnd, onExit }) => {
  const { language } = useLanguage();
  const [timeLeft, setTimeLeft] = useState(5);
  const [score, setScore] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);
  const [gameEnded, setGameEnded] = useState(false);
  const [clickedTargets, setClickedTargets] = useState<number[]>([]);
  const [targetPositions, setTargetPositions] = useState<number[]>([]);
  const [highScore, setHighScore] = useState(0);
  const [showSuccess, setShowSuccess] = useState(false);

  // ÈáçÁΩÆÈÅäÊà≤ÁãÄÊÖã
  const resetGame = () => {
    setTimeLeft(5);
    setScore(0);
    setGameStarted(false);
    setGameEnded(false);
    setClickedTargets([]);
    setTargetPositions([]);
    setShowSuccess(false);
    // ‰∏çÈáçÁΩÆÊúÄÈ´òÂàÜÔºå‰øùÊåÅË®òÈåÑ
  };

  // ÈñãÂßãÈÅäÊà≤
  const startGame = useCallback(() => {
    setGameStarted(true);
    setGameEnded(false);
    setScore(0);
    setTimeLeft(5);
    setClickedTargets([]);
    setTargetPositions(generateRandomTargets());
  }, []);

  // Áï∂ isActive ËÆäÁÇ∫ true ÊôÇÈáçÁΩÆÈÅäÊà≤
  useEffect(() => {
    if (isActive) {
      resetGame();
    }
  }, [isActive]);



  const handleButtonClick = (index: number) => {
    if (!gameStarted || gameEnded) return;

    // Ê™¢Êü•ÊòØÂê¶ÈªûÊìä‰∫ÜÁõÆÊ®ô‰ΩçÁΩÆ
    if (targetPositions.includes(index) && !clickedTargets.includes(index)) {
      const newClicked = [...clickedTargets, index];
      setClickedTargets(newClicked);
      setScore(prev => prev + 10);

      // Ê™¢Êü•ÊòØÂê¶ÂÆåÊàê‰∫ÜÊâÄÊúâÁõÆÊ®ô
      if (newClicked.length === targetPositions.length) {
        // Êõ¥Êñ∞ÊúÄÈ´òÂàÜ
        const newScore = score + 10;
        if (newScore > highScore) {
          setHighScore(newScore);
        }
        // ÁπºÁ∫å‰∏ã‰∏ÄËº™
        setTimeout(() => {
          setTimeLeft(5);
          setClickedTargets([]);
          setTargetPositions(generateRandomTargets());
        }, 1000);
      }
    }
  };

  // Ë®àÊôÇÂô®
  useEffect(() => {
    if (gameStarted && !gameEnded && timeLeft > 0) {
      const timer = setTimeout(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0 && gameStarted) {
      setGameEnded(true);
      // Êõ¥Êñ∞ÊúÄÈ´òÂàÜ
      if (score > highScore) {
        setHighScore(score);
      }
      // ‰∏çËá™ÂãïË™øÁî®onGameEndÔºåËÆìÁé©ÂÆ∂ÁúãÂà∞Game OverÁï´Èù¢
    }
  }, [gameStarted, gameEnded, timeLeft, score, highScore]);

  if (!isActive) return null;

  return (
    <div className="time-challenge-overlay" onClick={(e) => e.stopPropagation()}>
      <div className="time-challenge-game" onClick={(e) => e.stopPropagation()}>
        <div className="game-header">
          <h2 className="mini-game-title">
            {language === 'zh' ? '‚è±Ô∏è ÊôÇÈñìÊåëÊà∞' : '‚è±Ô∏è Time Challenge'}
          </h2>
          <button className="exit-button" onClick={onExit}>√ó</button>
        </div>

        <div className="game-info">
          <div className="time-display critical">
            ‚è∞ {timeLeft}s
          </div>
          <div className="score-display">
            üéØ {score}
          </div>
          <div className="high-score-display">
            {language === 'zh' ? 'ÊúÄÈ´òÂàÜ' : 'High Score'}: {highScore}
          </div>
        </div>


        {!gameStarted && (
          <div className="game-start">
            <h3>{language === 'zh' ? 'Ê∫ñÂÇôÂ•Ω‰∫ÜÂóéÔºü' : 'Ready to Play?'}</h3>
            <p>{language === 'zh' ? 'ÈªûÊìäÊâÄÊúâÂãæÂãæÔºÅ' : 'Click all checkmarks!'}</p>
            <button className="start-button" onClick={startGame}>
              {language === 'zh' ? 'ÈñãÂßãÊåëÊà∞' : 'Start Challenge'}
            </button>
          </div>
        )}

        {gameStarted && !gameEnded && (
          <>
            <div className="challenge-description">
              <h3>{language === 'zh' ? 'ÈªûÊìäÊâÄÊúâÂãæÂãæÔºÅ' : 'Click all checkmarks!'}</h3>
              <div className="progress">
                {clickedTargets.length} / {targetPositions.length}
              </div>
            </div>

            <div className="challenge-grid">
              {Array.from({ length: 20 }, (_, i) => {
                const isTarget = targetPositions.includes(i);
                const isClicked = clickedTargets.includes(i);
                return (
                  <button
                    key={i}
                    className={`challenge-button ${isTarget ? 'target' : 'decoy'} ${isClicked ? 'clicked' : ''}`}
                    style={{
                      backgroundColor: isTarget ? '#10b981' : '#666',
                      opacity: isClicked ? 0.5 : 1
                    }}
                    onClick={() => handleButtonClick(i)}
                    disabled={isClicked}
                  >
                    {isTarget ? '‚úì' : '‚úó'}
                  </button>
                );
              })}
            </div>
          </>
        )}

        {gameEnded && (
          <div className="game-end">
            <h3>{language === 'zh' ? 'ÊôÇÈñìÂà∞ÔºÅ' : 'Time\'s Up!'}</h3>
            <p>{language === 'zh' ? 'ÊúÄÁµÇÂàÜÊï∏' : 'Final Score'}: {score}</p>
            <div className="end-buttons">
              <button className="exit-button-full" onClick={onExit}>
                {language === 'zh' ? 'ËøîÂõûÈÅ∏ÂñÆ' : 'Back to Menu'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TimeChallengeGame;
