import React, { useState } from 'react';
import GameBoard from './components/GameBoard';
import GameStats from './components/GameStats';
import SettingsPanel from './components/SettingsPanel';
import ParticleSystem from './components/ParticleSystem';
import AchievementSystem from './components/AchievementSystem';
import LevelSelector from './components/LevelSelector';
import LevelCompleteModal from './components/LevelCompleteModal';
import DynamicBackground from './components/DynamicBackground';
import TimeDisplay from './components/TimeDisplay';
import NextBallPreview from './components/NextBallPreview';
import SpecialEffects from './components/SpecialEffects';
import { useGameState } from './hooks/useGameState';
import { GameState } from './types/game';
import { ThemeProvider } from './contexts/ThemeContext';
import { LanguageProvider, useLanguage } from './contexts/LanguageContext';
import { getNextLevel } from './types/levels';
import './App.css';

const GameContent: React.FC = () => {
  const { t } = useLanguage();
  const [showSettings, setShowSettings] = useState(false);
  const [showLevelSelector, setShowLevelSelector] = useState(false);
  const [particleEffect, setParticleEffect] = useState<{
    active: boolean;
    x: number;
    y: number;
    color: string;
  }>({ active: false, x: 0, y: 0, color: '#4ECDC4' });

  const {
    gameState,
    board,
    nextBall,
    stats,
    highScore,
    isNewHighScore,
    currentDifficulty,
    currentLevelConfig,
    levelComplete,
    timeRemaining,
    startGame,
    pauseGame,
    resumeGame,
    resetGame,
    nextLevel,
    setDifficulty,
    resetLevelComplete,
    handleCellClick
  } = useGameState();

  const handleAchievementUnlocked = () => {
    // Ëß∏ÁôºÁ≤íÂ≠êÊïàÊûú
    setParticleEffect({
      active: true,
      x: window.innerWidth / 2,
      y: window.innerHeight / 2,
      color: '#FFD700'
    });
    setTimeout(() => setParticleEffect(prev => ({ ...prev, active: false })), 100);

    // Êí≠ÊîæÊàêÂ∞±Èü≥Êïà
    playSound('levelUp');
  };

  // ÈÅäÊà≤ÈñãÂßãÊôÇÊí≠ÊîæËÉåÊôØÈü≥Ê®Ç
  React.useEffect(() => {
    if (gameState === GameState.PLAYING) {
      playBackgroundMusic();
    } else {
      stopBackgroundMusic();
    }
  }, [gameState, playBackgroundMusic, stopBackgroundMusic]);

  // Ê∑ªÂä†Èü≥ÊïàËß∏Áôº
  React.useEffect(() => {
    if (stats.combo > 1) {
      playSound('match');
    }
  }, [stats.combo, playSound]);

  const renderGameControls = () => {
    switch (gameState) {
      case GameState.MENU:
        return (
          <div className="game-controls">
            <h1 className="game-title">{t.gameTitle}</h1>
            <p className="game-description">
              {t.gameDescription}
            </p>
            <div className="menu-buttons">
              <button className="control-button start" onClick={() => startGame()}>
                {t.startGame}
              </button>
              <button className="control-button levels" onClick={() => setShowLevelSelector(true)}>
                {t.selectDifficulty}
              </button>
              <button className="control-button settings" onClick={() => setShowSettings(true)}>
                {t.settings}
              </button>
            </div>
          </div>
        );

      case GameState.PLAYING:
        return (
          <div className="game-controls">
            <button className="control-button pause" onClick={pauseGame}>
              {t.pauseGame}
            </button>
            <button className="control-button reset" onClick={resetGame}>
              {t.resetGame}
            </button>
            <button className="control-button settings" onClick={() => setShowSettings(true)}>
              {t.settings}
            </button>
          </div>
        );

      case GameState.PAUSED:
        return (
          <div className="game-controls">
            <h2 className="pause-title">{t.gamePaused}</h2>
            <button className="control-button resume" onClick={resumeGame}>
              {t.resumeGame}
            </button>
            <button className="control-button reset" onClick={resetGame}>
              {t.resetGame}
            </button>
            <button className="control-button settings" onClick={() => setShowSettings(true)}>
              {t.settings}
            </button>
          </div>
        );

      case GameState.GAME_OVER:
        return (
          <div className="game-controls">
            <h2 className="game-over-title">{t.gameOver}</h2>
            {isNewHighScore && (
              <div className="new-high-score">
                üéâ {t.newHighScore} üéâ
              </div>
            )}
            <div className="final-stats">
              <p>{t.finalScore}: <span className="highlight">{stats.score.toLocaleString()}</span></p>
              <p>{t.level}: <span className="highlight">{stats.level}</span></p>
              <p>{t.gameTime}: <span className="highlight">{Math.floor(stats.timeElapsed / 60)}:{(stats.timeElapsed % 60).toString().padStart(2, '0')}</span></p>
              <p>{t.highScore}: <span className="highlight">{highScore.score.toLocaleString()}</span></p>
            </div>
            <button className="control-button restart" onClick={() => startGame()}>
              {t.playAgain}
            </button>
            <button className="control-button menu" onClick={resetGame}>
              {t.backToMenu}
            </button>
          </div>
        );

      default:
        return null;
    }
  };

  const renderGameInstructions = () => {
    if (gameState === GameState.MENU) {
      return (
        <div className="game-instructions">
          <h3>{t.instructions}</h3>
          <ul>
            <li>{t.instructionsList.clickToPlace}</li>
            <li>{t.instructionsList.matchThree}</li>
            <li>{t.instructionsList.chainReaction}</li>
            <li>{t.instructionsList.specialBalls}</li>
            <li>{t.instructionsList.autoDrops}</li>
            <li>{t.instructionsList.challenge}</li>
          </ul>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="app">
      {/* ÂãïÊÖãËÉåÊôØ */}
      <DynamicBackground level={currentLevelConfig} />

      <div className="app-container">
        {gameState === GameState.MENU ? (
          <div className="menu-layout">
            {renderGameControls()}
            {renderGameInstructions()}
          </div>
        ) : (
          <div className="game-layout">
            <div className="game-area">
              <GameBoard 
                board={board}
                onCellClick={handleCellClick}
                nextBall={nextBall || undefined}
              />
            </div>

            <div className="sidebar">
              <TimeDisplay timeRemaining={timeRemaining} />
              <NextBallPreview nextBall={nextBall} />
              <GameStats stats={stats} />
              {renderGameControls()}
            </div>
          </div>
        )}

        {(gameState === GameState.PAUSED || gameState === GameState.GAME_OVER) && (
          <div className="overlay">
            <div className="overlay-content">
              {renderGameControls()}
            </div>
          </div>
        )}
      </div>

      {/* Ë®≠ÂÆöÈù¢Êùø */}
      <SettingsPanel 
        isOpen={showSettings} 
        onClose={() => setShowSettings(false)} 
      />

      {/* ÈóúÂç°ÈÅ∏ÊìáÂô® */}
      <LevelSelector 
        currentLevel={currentDifficulty}
        highScore={highScore.score}
        onLevelSelect={(level) => {
          console.log('Level selected:', level);
          setDifficulty(level);
          setShowLevelSelector(false);
          // ÈÅ∏ÊìáÈóúÂç°ÂæåËá™ÂãïÈñãÂßãÈÅäÊà≤
          startGame(level);
        }}
        onClose={() => setShowLevelSelector(false)}
        isOpen={showLevelSelector}
      />

      {/* ÈóúÂç°ÂÆåÊàêÊ®°ÊÖãÊ°Ü */}
      <LevelCompleteModal 
        isOpen={levelComplete}
        currentLevel={currentLevelConfig}
        nextLevel={getNextLevel(currentDifficulty)}
        score={stats.score}
        onNextLevel={() => {
          console.log('Next level clicked');
          nextLevel();
          setParticleEffect({
            active: true,
            x: window.innerWidth / 2,
            y: window.innerHeight / 2,
            color: '#FFD700'
          });
          setTimeout(() => setParticleEffect(prev => ({ ...prev, active: false })), 100);
        }}
        onReplay={() => {
          resetLevelComplete();
          startGame(currentDifficulty);
        }}
        onMenu={() => {
          resetLevelComplete();
          resetGame();
        }}
      />

      {/* Á≤íÂ≠êÊïàÊûú */}
      <ParticleSystem 
        isActive={particleEffect.active}
        x={particleEffect.x}
        y={particleEffect.y}
        color={particleEffect.color}
        type="sparkle"
      />

      {/* ÊàêÂ∞±Á≥ªÁµ± */}
      <AchievementSystem 
        stats={stats}
        onAchievementUnlocked={handleAchievementUnlocked}
      />

      {/* ÁâπÊÆäÊïàÊûú */}
      <SpecialEffects 
        combo={stats.combo}
        score={stats.score}
        timeRemaining={timeRemaining}
      />
    </div>
  );
};

const App: React.FC = () => {
  return (
