import React from 'react';
import { LevelConfig } from '../types/levels';
import { useLanguage } from '../contexts/LanguageContext';
import './LevelCompleteModal.css';

interface LevelCompleteModalProps {
  isOpen: boolean;
  currentLevel: LevelConfig;
  nextLevel: LevelConfig | null;
  score: number;
  onNextLevel: () => void;
  onReplay: () => void;
  onMenu: () => void;
}

const LevelCompleteModal: React.FC<LevelCompleteModalProps> = ({
  isOpen,
  currentLevel,
  nextLevel,
  score,
  onNextLevel,
  onReplay,
  onMenu
}) => {
  const { language } = useLanguage();

  if (!isOpen) return null;

  console.log('LevelCompleteModal rendered', { isOpen, currentLevel, nextLevel, score });

  const getDifficultyIcon = (difficulty: string) => {
    switch (difficulty) {
      case 'easy': return 'üå±';
      case 'medium': return 'üî•';
      case 'hard': return '‚ö°';
      case 'super': return 'üíé';
      case 'god': return 'üëë';
      default: return 'üéÆ';
    }
  };

  return (
    <div className="level-complete-overlay">
      <div className="level-complete-modal">
        <div className="celebration-header">
          <div className="celebration-icon">üéâ</div>
          <h2 className="celebration-title">
            {language === 'zh' ? 'ÈóúÂç°ÂÆåÊàêÔºÅ' : 'Level Complete!'}
          </h2>
          <div className="level-badge">
            <span className="level-icon">{getDifficultyIcon(currentLevel.id)}</span>
            <span className="level-name">
              {language === 'zh' ? currentLevel.name : currentLevel.nameEn}
            </span>
          </div>

          {/* Ë™øË©¶‰ø°ÊÅØÁßªÂà∞È†ÇÈÉ® */}
          <div style={{ background: 'yellow', color: 'black', padding: '10px', margin: '10px 0', borderRadius: '5px' }}>
            <div><strong>DEBUG INFO:</strong></div>
            <div>Current: {currentLevel.name}</div>
            <div>Next: {nextLevel ? nextLevel.name : '‚ùå NULL'}</div>
            <div>Score: {score}</div>
          </div>
        </div>

        <div className="completion-stats">
          <div className="stat-item">
            <span className="stat-label">
              {language === 'zh' ? 'ÊúÄÁµÇÂàÜÊï∏' : 'Final Score'}
            </span>
            <span className="stat-value score">{score.toLocaleString()}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">
              {language === 'zh' ? 'ÁõÆÊ®ôÂàÜÊï∏' : 'Target Score'}
            </span>
            <span className="stat-value target">{currentLevel.targetScore.toLocaleString()}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">
              {language === 'zh' ? 'Ë∂ÖË∂äÁõÆÊ®ô' : 'Exceeded By'}
            </span>
            <span className="stat-value bonus">
              +{(score - currentLevel.targetScore).toLocaleString()}
            </span>
          </div>
        </div>

        {nextLevel && (
          <div className="next-level-preview">
            <h3 className="next-level-title">
              {language === 'zh' ? '‰∏ã‰∏ÄÂÄãÊåëÊà∞' : 'Next Challenge'}
            </h3>
            <div className="next-level-card">
              <div className="next-level-icon">
                {getDifficultyIcon(nextLevel.id)}
              </div>
              <div className="next-level-info">
                <h4 className="next-level-name">
                  {language === 'zh' ? nextLevel.name : nextLevel.nameEn}
                </h4>
                <p className="next-level-desc">
                  {language === 'zh' ? nextLevel.description : nextLevel.descriptionEn}
                </p>
                <div className="next-level-target">
                  {language === 'zh' ? 'ÁõÆÊ®ô' : 'Target'}: {nextLevel.targetScore.toLocaleString()}
                </div>
              </div>
            </div>
          </div>
        )}

        <div className="completion-actions">
          {/* Ë™øË©¶‰ø°ÊÅØ */}
          <div style={{ background: 'yellow', padding: '10px', margin: '10px 0' }}>
            <div>Current Level: {currentLevel.name}</div>
            <div>Next Level: {nextLevel ? nextLevel.name : 'NULL'}</div>
            <div>Score: {score}</div>
          </div>

          {nextLevel ? (
            <>
              {/* Á∞°ÂåñÁöÑÊ∏¨Ë©¶ÊåâÈàï */}
              <button 
                onClick={() => {
                  console.log('SIMPLE BUTTON CLICKED!');
                  alert('Simple button works!');
                  onNextLevel();
                }}
                style={{ 
                  padding: '20px', 
                  fontSize: '16px', 
                  backgroundColor: 'green', 
                  color: 'white',
                  border: 'none',
                  cursor: 'pointer',
                  zIndex: 9999,
                  position: 'relative'
                }}
              >
                üöÄ SIMPLE NEXT LEVEL BUTTON
              </button>

              {/* ÂéüÂßãÊåâÈàï */}
              <button 
                className="action-button primary" 
                onClick={(e) => {
                  console.log('Original button clicked!', e);
                  onNextLevel();
                }}
              >
                <span className="button-icon">üöÄ</span>
                <span>{language === 'zh' ? 'ÊåëÊà∞‰∏ã‰∏ÄÈóú' : 'Next Level'}</span>
              </button>
            </>
          ) : (
            <div style={{ color: 'red', padding: '10px', fontSize: '18px', fontWeight: 'bold' }}>
              ‚ùå NO NEXT LEVEL AVAILABLE (nextLevel is null)
            </div>
          )}

          <button className="action-button secondary" onClick={onReplay}>
            <span className="button-icon">üîÑ</span>
            <span>{language === 'zh' ? 'ÈáçÊñ∞ÊåëÊà∞' : 'Replay'}</span>
          </button>

          <button className="action-button tertiary" onClick={onMenu}>
            <span className="button-icon">üè†</span>
            <span>{language === 'zh' ? 'ÂõûÂà∞‰∏ªÈÅ∏ÂñÆ' : 'Main Menu'}</span>
          </button>

          {/* Ê∏¨Ë©¶ÊåâÈàï */}
          <button 
            className="action-button primary" 
            onClick={() => {
              console.log('Test button clicked!');
              alert('Test button works!');
            }}
            style={{ backgroundColor: 'red', zIndex: 1001 }}
          >
            <span>üß™ Test Button</span>
          </button>
        </div>

        {!nextLevel && (
          <div className="all-complete-message">
            <div className="master-badge">
              <span className="master-icon">üèÜ</span>
              <span className="master-text">
                {language === 'zh' ? 'ÊÅ≠ÂñúÔºÅ‰Ω†Â∑≤ÂÆåÊàêÊâÄÊúâÊåëÊà∞ÔºÅ' : 'Congratulations! You\'ve mastered all levels!'}
              </span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default LevelCompleteModal;
