import React from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import './MiniGamesMenu.css';

interface MiniGame {
  id: string;
  title: string;
  titleEn: string;
  description: string;
  descriptionEn: string;
  icon: string;
  difficulty: 'Easy' | 'Medium' | 'Hard';
  estimatedTime: string;
  estimatedTimeEn: string;
}

const miniGames: MiniGame[] = [
  {
    id: 'color_memory',
    title: 'Ëâ≤ÂΩ©Ë®òÊÜ∂',
    titleEn: 'Color Memory',
    description: 'Ë®ò‰ΩèÈ°èËâ≤Â∫èÂàó‰∏¶ÈáçË§áÂÆÉÂÄë',
    descriptionEn: 'Remember color sequences and repeat them',
    icon: 'üß†',
    difficulty: 'Easy',
    estimatedTime: '2-3ÂàÜÈêò',
    estimatedTimeEn: '2-3 minutes',
  },
  {
    id: 'speed_match',
    title: 'Âø´ÈÄüÂåπÈÖç',
    titleEn: 'Speed Match',
    description: 'Âú®ÈôêÊôÇÂÖßÂåπÈÖçÁõ°ÂèØËÉΩÂ§öÁöÑÈ°èËâ≤',
    descriptionEn: 'Match as many colors as possible within time limit',
    icon: '‚ö°',
    difficulty: 'Medium',
    estimatedTime: '1-2ÂàÜÈêò',
    estimatedTimeEn: '1-2 minutes',
  },
  {
    id: 'pattern_puzzle',
    title: 'ÂúñÊ°àÊãºÂúñ',
    titleEn: 'Pattern Puzzle',
    description: 'ÈáçÁèæÁµ¶ÂÆöÁöÑÈ°èËâ≤ÂúñÊ°à',
    descriptionEn: 'Recreate the given color patterns',
    icon: 'üß©',
    difficulty: 'Medium',
    estimatedTime: '3-5ÂàÜÈêò',
    estimatedTimeEn: '3-5 minutes',
  },
  {
    id: 'pattern_puzzle',
    title: 'ÂúñÊ°àÊãºÂúñ',
    titleEn: 'Pattern Puzzle',
    description: 'Ë®ò‰Ωè‰∏¶ÈáçÁèæÈ°èËâ≤ÂúñÊ°à',
    descriptionEn: 'Remember and recreate color patterns',
    icon: 'üß©',
    difficulty: 'Medium',
    estimatedTime: '2-4ÂàÜÈêò',
    estimatedTimeEn: '2-4 minutes',
  },
  {
    id: 'time_challenge',
    title: 'ÊôÇÈñìÊåëÊà∞',
    titleEn: 'Time Challenge',
    description: 'Âú®Ê•µÁü≠ÊôÇÈñìÂÖßÂÆåÊàêË§áÈõú‰ªªÂãô',
    descriptionEn: 'Complete complex tasks in very short time',
    icon: '‚è±Ô∏è',
    difficulty: 'Hard',
    estimatedTime: '30Áßí-1ÂàÜÈêò',
    estimatedTimeEn: '30s-1 minute',
  },
];

interface MiniGamesMenuProps {
  isOpen: boolean;
  onClose: () => void;
  onGameSelect: (gameId: string) => void;
}

const MiniGamesMenu: React.FC<MiniGamesMenuProps> = ({ isOpen, onClose, onGameSelect }) => {
  const { language } = useLanguage();

  if (!isOpen) return null;

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty) {
      case 'Easy': return '#10b981';
      case 'Medium': return '#f59e0b';
      case 'Hard': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const getDifficultyIcon = (difficulty: string) => {
    switch (difficulty) {
      case 'Easy': return 'üü¢';
      case 'Medium': return 'üü°';
      case 'Hard': return 'üî¥';
      default: return '‚ö™';
    }
  };

  return (
    <div className="mini-games-overlay" onClick={onClose}>
      <div className="mini-games-panel" onClick={(e) => e.stopPropagation()}>
        <div className="mini-games-header">
          <h2 className="mini-games-title">
            {language === 'zh' ? 'Â∞èÈÅäÊà≤ÈÅ∏Êìá' : 'Mini Games'}
          </h2>
          <button className="close-button" onClick={onClose}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path d="M18 6L6 18M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>
        </div>

        <div className="mini-games-grid">
          {miniGames.map((game, index) => (
            <div
              key={game.id}
              className="mini-game-card"
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                console.log('Mini game selected:', game.id);
                onGameSelect(game.id);
              }}
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <div className="game-icon">{game.icon}</div>
              
              <div className="game-info">
                <h3 className="game-title">
                  {language === 'zh' ? game.title : game.titleEn}
                </h3>
                <p className="game-description">
                  {language === 'zh' ? game.description : game.descriptionEn}
                </p>
                
                <div className="game-meta">
                  <div className="difficulty-badge" style={{ backgroundColor: getDifficultyColor(game.difficulty) }}>
                    <span className="difficulty-icon">{getDifficultyIcon(game.difficulty)}</span>
                    <span>{game.difficulty}</span>
                  </div>
                  <div className="time-estimate">
                    <span className="time-icon">‚è∞</span>
                    <span>{language === 'zh' ? game.estimatedTime : game.estimatedTimeEn}</span>
                  </div>
                </div>
              </div>

              <div className="play-button">
                <span>‚ñ∂Ô∏è</span>
              </div>

              <div className="card-glow"></div>
              
              {/* Ê∑ªÂä†ÊòéÈ°ØÁöÑÈªûÊìäÊèêÁ§∫ */}
              <div className="click-hint">
                {language === 'zh' ? 'ÈªûÊìäÈñãÂßã' : 'Click to Play'}
              </div>
              
              {/* Ê∏¨Ë©¶Áî®ÁöÑÁ∞°ÂñÆÊåâÈàï */}
              <button 
                className="simple-play-button"
                onClick={(e) => {
                  e.preventDefault();
                  e.stopPropagation();
                  console.log('Simple button clicked for game:', game.id);
                  onGameSelect(game.id);
                }}
                style={{
                  position: 'absolute',
                  bottom: '8px',
                  right: '8px',
                  background: '#ff0000',
                  color: 'white',
                  border: 'none',
                  padding: '8px 12px',
                  borderRadius: '8px',
                  fontSize: '12px',
                  cursor: 'pointer',
                  zIndex: 100
                }}
              >
                PLAY
              </button>
            </div>
          ))}
        </div>

        <div className="mini-games-footer">
          <p className="footer-text">
            {language === 'zh' 
              ? 'ÈÅ∏Êìá‰∏ÄÂÄãÂ∞èÈÅäÊà≤‰æÜÊîæÈ¨Ü‰∏Ä‰∏ãÔºåÊàñËÄÖÁ∑¥Áøí‰Ω†ÁöÑÊäÄËÉΩÔºÅ' 
              : 'Choose a mini-game to relax or practice your skills!'
            }
          </p>
        </div>
      </div>
    </div>
  );
};

export default MiniGamesMenu;
