import React, { useState, useEffect, useCallback } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import './SpeedMatchGame.css';

interface SpeedMatchGameProps {
  isActive: boolean;
  onGameEnd: (score: number) => void;
  onExit: () => void;
}

const colors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#FFA726', '#AB47BC', '#FF7043'];
const shapes = ['‚óè', '‚ñ†', '‚ñ≤', '‚ô¶', '‚òÖ', '‚ô†'];

interface Target {
  color: string;
  shape: string;
}

const SpeedMatchGame: React.FC<SpeedMatchGameProps> = ({ isActive, onGameEnd, onExit }) => {
  const { language } = useLanguage();
  const [timeLeft, setTimeLeft] = useState(60);
  const [score, setScore] = useState(0);
  const [target, setTarget] = useState<Target>({ color: colors[0], shape: shapes[0] });
  const [gameStarted, setGameStarted] = useState(false);
  const [gameEnded, setGameEnded] = useState(false);

  // ÈáçÁΩÆÈÅäÊà≤ÁãÄÊÖã
  const resetGame = () => {
    setTimeLeft(60);
    setScore(0);
    setTarget({ color: colors[0], shape: shapes[0] });
    setGameStarted(false);
    setGameEnded(false);
  };

  // Áï∂ isActive ËÆäÁÇ∫ true ÊôÇÈáçÁΩÆ‰∏¶Ëá™ÂãïÈñãÂßãÈÅäÊà≤
  useEffect(() => {
    if (isActive) {
      resetGame();
      // Ëá™ÂãïÈñãÂßãÈÅäÊà≤
      setTimeout(() => {
        startGame();
      }, 100);
    }
  }, [isActive]);

  // ÁîüÊàêÊñ∞ÁõÆÊ®ô
  const generateNewTarget = useCallback(() => {
    const newTarget = {
      color: colors[Math.floor(Math.random() * colors.length)],
      shape: shapes[Math.floor(Math.random() * shapes.length)]
    };
    setTarget(newTarget);
  }, []);

  // ÈñãÂßãÈÅäÊà≤
  const startGame = () => {
    setGameStarted(true);
    setGameEnded(false);
    setScore(0);
    setTimeLeft(60);
    generateNewTarget();
  };

  // ËôïÁêÜÂåπÈÖçÈªûÊìä
  const handleMatch = (color: string, shape: string) => {
    if (!gameStarted || gameEnded) return;

    if (color === target.color && shape === target.shape) {
      // Ê≠£Á¢∫ÂåπÈÖç
      setScore(prev => prev + 10);
      generateNewTarget();
    } else {
      // ÈåØË™§ÂåπÈÖçÔºåÊâ£ÂàÜ
      setScore(prev => Math.max(0, prev - 5));
    }
  };

  // Ë®àÊôÇÂô®
  useEffect(() => {
    if (gameStarted && !gameEnded && timeLeft > 0) {
      const timer = setTimeout(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0 && gameStarted) {
      setGameEnded(true);
      onGameEnd(score);
    }
  }, [gameStarted, gameEnded, timeLeft, score, onGameEnd]);

  console.log('SpeedMatchGame render:', { isActive, gameStarted, gameEnded });

  if (!isActive) return null;

  return (
    <div className="speed-match-overlay">
      <div className="speed-match-game">
        <div className="game-header">
          <h2 className="game-title">
            {language === 'zh' ? '‚ö° Âø´ÈÄüÂåπÈÖç' : '‚ö° Speed Match'}
          </h2>
          <button 
            className="exit-button" 
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              console.log('Exit button clicked in SpeedMatchGame');
              onExit();
            }}
          >
            √ó
          </button>
        </div>

        <div className="game-info">
          <div className="time-display">
            ‚è∞ {timeLeft}s
          </div>
          <div className="score-display">
            üéØ {score}
          </div>
        </div>

        {!gameStarted && !gameEnded && (
          <div className="game-start">
            <h3>{language === 'zh' ? 'Ê∫ñÂÇôÂ•Ω‰∫ÜÂóéÔºü' : 'Ready?'}</h3>
            <p>
              {language === 'zh' 
                ? 'Âú®60ÁßíÂÖßÈªûÊìäËàáÁõÆÊ®ôÁõ∏ÂêåÁöÑÈ°èËâ≤ÂíåÂΩ¢ÁãÄÁµÑÂêàÔºÅ' 
                : 'Click the same color and shape combination as the target within 60 seconds!'
              }
            </p>
            <button className="start-button" onClick={startGame}>
              {language === 'zh' ? 'ÈñãÂßãÊåëÊà∞' : 'Start Challenge'}
            </button>
          </div>
        )}

        {gameStarted && !gameEnded && (
          <>
            <div className="target-display">
              <h3>{language === 'zh' ? 'ÊâæÂà∞ÈÄôÂÄãÁµÑÂêàÔºö' : 'Find this combination:'}</h3>
              <div 
                className="target-item"
                style={{ color: target.color }}
              >
                {target.shape}
              </div>
            </div>

            <div className="match-grid">
              {colors.map(color => 
                shapes.map(shape => (
                  <button
                    key={`${color}-${shape}`}
                    className="match-button"
                    style={{ color }}
                    onClick={() => handleMatch(color, shape)}
                  >
                    {shape}
                  </button>
                ))
              )}
            </div>
          </>
        )}

        {gameEnded && (
          <div className="game-end">
            <h3>{language === 'zh' ? 'ÊôÇÈñìÂà∞ÔºÅ' : 'Time\'s Up!'}</h3>
            <p>{language === 'zh' ? 'ÊúÄÁµÇÂàÜÊï∏' : 'Final Score'}: {score}</p>
            <div className="end-buttons">
              <button className="restart-button" onClick={startGame}>
                {language === 'zh' ? 'ÂÜçÊåëÊà∞‰∏ÄÊ¨°' : 'Try Again'}
              </button>
              <button className="exit-button-full" onClick={onExit}>
                {language === 'zh' ? 'ËøîÂõûÈÅ∏ÂñÆ' : 'Back to Menu'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SpeedMatchGame;
