import React, { useState, useCallback, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import './ColorMemoryGame.css';

interface ColorMemoryGameProps {
  isActive: boolean;
  onGameEnd: (score: number) => void;
  onExit: () => void;
}

const colors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#FFA726', '#AB47BC', '#FF7043'];

const ColorMemoryGame: React.FC<ColorMemoryGameProps> = ({ isActive, onGameEnd, onExit }) => {
  const { language } = useLanguage();
  const [sequence, setSequence] = useState<string[]>([]);
  const [playerSequence, setPlayerSequence] = useState<string[]>([]);
  const [showingSequence, setShowingSequence] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [score, setScore] = useState(0);
  const [gameState, setGameState] = useState<'waiting' | 'showing' | 'input' | 'correct' | 'wrong' | 'ended'>('waiting');
  const [gameStarted, setGameStarted] = useState(false);
  const [highlightedColor, setHighlightedColor] = useState<string | null>(null);
  const [highScore, setHighScore] = useState(0);

  // ÈáçÁΩÆÈÅäÊà≤ÁãÄÊÖã
  const resetGame = () => {
    setSequence([]);
    setPlayerSequence([]);
    setShowingSequence(false);
    setCurrentStep(0);
    setScore(0);
    setGameState('waiting');
    setGameStarted(false);
    setHighlightedColor(null);
    // ‰∏çÈáçÁΩÆÊúÄÈ´òÂàÜÔºå‰øùÊåÅË®òÈåÑ
  };

  // ÈñãÂßãÊñ∞‰∏ÄËº™
  const startNewRound = useCallback(() => {
    // ÁîüÊàêÂÖ®Êñ∞ÁöÑÈö®Ê©üÂ∫èÂàóÔºåÈï∑Â∫¶ÁÇ∫Áï∂ÂâçÂõûÂêàÊï∏+1
    const newSequenceLength = sequence.length + 1;
    const newSequence = [];
    for (let i = 0; i < newSequenceLength; i++) {
      newSequence.push(colors[Math.floor(Math.random() * colors.length)]);
    }

    setSequence(newSequence);
    setPlayerSequence([]);
    setCurrentStep(0);
    setGameState('showing');
    setShowingSequence(true);

    // È°ØÁ§∫Â∫èÂàó
    newSequence.forEach((color, index) => {
      setTimeout(() => {
        setHighlightedColor(color);
        setTimeout(() => setHighlightedColor(null), 600);
      }, index * 800);
    });

    // Â∫èÂàóÈ°ØÁ§∫ÂÆåÁï¢ÂæåÂÖÅË®±Áé©ÂÆ∂Ëº∏ÂÖ•
    setTimeout(() => {
      setShowingSequence(false);
      setGameState('input');
    }, newSequence.length * 800 + 500);
  }, [sequence.length]);

  // ÈñãÂßãÈÅäÊà≤
  const startGame = useCallback(() => {
    setSequence([]);
    setPlayerSequence([]);
    setScore(0);
    setCurrentStep(0);
    setGameState('waiting');
    setGameStarted(true);
    startNewRound();
  }, [startNewRound]);

  // Áï∂ isActive ËÆäÁÇ∫ true ÊôÇÈáçÁΩÆÈÅäÊà≤
  useEffect(() => {
    if (isActive) {
      resetGame();
    }
  }, [isActive]);


  // ËôïÁêÜÁé©ÂÆ∂ÈªûÊìä
  const handleColorClick = (color: string) => {
    if (gameState !== 'input' || showingSequence) return;

    const newPlayerSequence = [...playerSequence, color];
    setPlayerSequence(newPlayerSequence);

    // Ê™¢Êü•ÊòØÂê¶Ê≠£Á¢∫
    if (color === sequence[currentStep]) {
      setCurrentStep(currentStep + 1);

      // Ê™¢Êü•ÊòØÂê¶ÂÆåÊàêÈÄô‰∏ÄËº™
      if (currentStep + 1 === sequence.length) {
        setScore(score + sequence.length * 10);
        setGameState('correct');

        setTimeout(() => {
          startNewRound();
        }, 1000);
      }
     } else {
       // ÈåØË™§
       setGameState('wrong');
       setTimeout(() => {
         setGameState('ended');
         // Êõ¥Êñ∞ÊúÄÈ´òÂàÜ
         if (score > highScore) {
           setHighScore(score);
         }
         // ‰∏çËá™ÂãïË™øÁî®onGameEndÔºåËÆìÁé©ÂÆ∂ÁúãÂà∞Game OverÁï´Èù¢
       }, 1500);
     }
  };


  if (!isActive) return null;

  return (
    <div className="color-memory-overlay" onClick={(e) => e.stopPropagation()}>
      <div className="color-memory-game" onClick={(e) => e.stopPropagation()}>
        <div className="game-header">
          <h2 className="mini-game-title">
            {language === 'zh' ? 'üß† Ëâ≤ÂΩ©Ë®òÊÜ∂' : 'üß† Color Memory'}
          </h2>
          <button className="exit-button" onClick={onExit}>√ó</button>
        </div>

        <div className="game-info">
          <div className="score-display">
            {language === 'zh' ? 'ÂàÜÊï∏' : 'Score'}: {score}
          </div>
          <div className="round-display">
            {language === 'zh' ? 'ÂõûÂêà' : 'Round'}: {sequence.length}
          </div>
          <div className="high-score-display">
            {language === 'zh' ? 'ÊúÄÈ´òÂàÜ' : 'High Score'}: {highScore}
          </div>
          {gameStarted && (
            <div className="time-display">
              ‚è∞ {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}
            </div>
          )}
        </div>


        {gameState === 'showing' && (
          <div className="sequence-display">
            <h3>{language === 'zh' ? 'Ë®ò‰ΩèÈÄôÂÄãÂ∫èÂàó...' : 'Remember this sequence...'}</h3>
          </div>
        )}

        {gameState === 'input' && (
          <div className="input-prompt">
            <h3>{language === 'zh' ? 'ÈáçË§áÂ∫èÂàóÔºÅ' : 'Repeat the sequence!'}</h3>
            <div className="progress">
              {currentStep} / {sequence.length}
            </div>
          </div>
        )}

        {gameState === 'correct' && (
          <div className="feedback correct">
            <h3>‚úÖ {language === 'zh' ? 'Ê≠£Á¢∫ÔºÅ' : 'Correct!'}</h3>
          </div>
        )}

        {gameState === 'wrong' && (
          <div className="feedback wrong">
            <h3>‚ùå {language === 'zh' ? 'ÈåØË™§ÔºÅ' : 'Wrong!'}</h3>
          </div>
        )}

        {!gameStarted && (
          <div className="game-start">
            <h3>{language === 'zh' ? 'Ê∫ñÂÇôÂ•Ω‰∫ÜÂóéÔºü' : 'Ready to Play?'}</h3>
            <p>{language === 'zh' ? 'Ë®ò‰ΩèÈ°èËâ≤Â∫èÂàó‰∏¶ÈáçË§áÂÆÉÂÄë' : 'Remember color sequences and repeat them'}</p>
            <button className="start-button" onClick={startGame}>
              {language === 'zh' ? 'ÈñãÂßãÈÅäÊà≤' : 'Start Game'}
            </button>
          </div>
        )}

        {gameState === 'ended' && (
          <div className="game-end">
            <h3>{language === 'zh' ? 'ÈÅäÊà≤ÁµêÊùüÔºÅ' : 'Game Over!'}</h3>
            <p>{language === 'zh' ? 'ÊúÄÁµÇÂàÜÊï∏' : 'Final Score'}: {score}</p>
            <p>{language === 'zh' ? 'ÈÅîÂà∞ÂõûÂêà' : 'Rounds Reached'}: {sequence.length}</p>
             <div className="end-buttons">
               <button className="exit-button-full" onClick={onExit}>
                 {language === 'zh' ? 'ËøîÂõûÈÅ∏ÂñÆ' : 'Back to Menu'}
               </button>
             </div>
          </div>
        )}

        {/* È°èËâ≤ÊåâÈàï */}
        <div className="color-grid">
          {colors.map((color) => (
            <button
              key={color}
              className={`color-button ${highlightedColor === color ? 'highlighted' : ''}`}
              style={{ backgroundColor: color }}
              onClick={() => handleColorClick(color)}
              disabled={gameState === 'showing' || gameState === 'waiting' || gameState === 'ended'}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ColorMemoryGame;
