import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import './ComboRewards.css';

interface ComboRewardsProps {
  combo: number;
  onRewardClaimed?: (reward: string) => void;
}

interface Reward {
  id: string;
  name: string;
  nameEn: string;
  description: string;
  descriptionEn: string;
  icon: string;
  comboRequired: number;
  effect: string;
}

const rewards: Reward[] = [
  {
    id: 'time_bonus',
    name: 'ÊôÇÈñìÁçéÂãµ',
    nameEn: 'Time Bonus',
    description: '+15ÁßíÊôÇÈñì',
    descriptionEn: '+15 seconds',
    icon: '‚è∞',
    comboRequired: 5,
    effect: 'addTime',
  },
  {
    id: 'score_bonus',
    name: 'ÂàÜÊï∏ÁçéÂãµ',
    nameEn: 'Score Bonus',
    description: '+500ÂàÜÁçéÂãµ',
    descriptionEn: '+500 points',
    icon: 'üí∞',
    comboRequired: 7,
    effect: 'scoreBonus',
  },
];

const ComboRewards: React.FC<ComboRewardsProps> = ({ combo, onRewardClaimed }) => {
  const { language } = useLanguage();
  const [availableRewards, setAvailableRewards] = useState<Reward[]>([]);
  const [showRewards, setShowRewards] = useState(false);

  useEffect(() => {
    const newRewards = rewards.filter(reward => 
      combo >= reward.comboRequired && combo % reward.comboRequired === 0
    );
    
    if (newRewards.length > 0) {
      setAvailableRewards(newRewards);
      setShowRewards(true);
      setTimeout(() => setShowRewards(false), 5000); // 5ÁßíÂæåËá™ÂãïÈö±Ëóè
    }
  }, [combo]);

  const claimReward = (reward: Reward) => {
    if (onRewardClaimed) {
      onRewardClaimed(reward.effect);
    }
    setShowRewards(false);
  };

  if (!showRewards || availableRewards.length === 0) return null;

  return (
    <div className="combo-rewards-overlay">
      <div className="combo-rewards-panel">
        <div className="rewards-header">
          <h3 className="rewards-title">
            üéÅ {language === 'zh' ? 'ÈÄ£ÊìäÁçéÂãµÔºÅ' : 'Combo Rewards!'}
          </h3>
          <div className="combo-badge">
            {combo}x COMBO!
          </div>
        </div>
        
        <div className="rewards-grid">
          {availableRewards.map(reward => (
            <div
              key={reward.id}
              className="reward-card"
              onClick={() => claimReward(reward)}
            >
              <div className="reward-icon">{reward.icon}</div>
              <div className="reward-info">
                <h4 className="reward-name">
                  {language === 'zh' ? reward.name : reward.nameEn}
                </h4>
                <p className="reward-description">
                  {language === 'zh' ? reward.description : reward.descriptionEn}
                </p>
              </div>
            </div>
          ))}
        </div>
        
        <div className="rewards-footer">
          <button 
            className="skip-button"
            onClick={() => setShowRewards(false)}
          >
            {language === 'zh' ? 'Ë∑≥ÈÅé' : 'Skip'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ComboRewards;
