import { useState, useEffect } from 'react';

interface HighScoreData {
  score: number;
  level: number;
  date: string;
  playerName?: string;
}

export const useHighScore = () => {
  const [highScore, setHighScore] = useState<HighScoreData>({
    score: 0,
    level: 1,
    date: new Date().toISOString(),
  });

  const [isNewHighScore, setIsNewHighScore] = useState(false);

  // 載入最高分 - 改為不從 localStorage 載入，每次重新整理都歸零
  useEffect(() => {
    // 不再從 localStorage 載入最高分，保持初始值 0
    // 這樣每次重新整理後最高分都會歸零
  }, []);

  // 檢查並更新最高分
  const checkAndUpdateHighScore = (newScore: number, newLevel: number) => {
    if (newScore > highScore.score) {
      const newHighScore: HighScoreData = {
        score: newScore,
        level: newLevel,
        date: new Date().toISOString(),
      };
      
      setHighScore(newHighScore);
      // 不再保存到 localStorage，讓最高分在重新整理後歸零
      setIsNewHighScore(true);
      
      // 3秒後重置新紀錄標記
      setTimeout(() => setIsNewHighScore(false), 3000);
      
      return true;
    }
    return false;
  };

  const resetHighScore = () => {
    const resetData: HighScoreData = {
      score: 0,
      level: 1,
      date: new Date().toISOString(),
    };
    setHighScore(resetData);
    localStorage.setItem('highScore', JSON.stringify(resetData));
  };

  return {
    highScore,
    isNewHighScore,
    checkAndUpdateHighScore,
    resetHighScore,
  };
};
