import React from 'react';
import GameBoard from './components/GameBoard';
import GameStats from './components/GameStats';
import { useGameState } from './hooks/useGameState';
import { GameState } from './types/game';
import './App.css';

const App: React.FC = () => {
  const {
    gameState,
    board,
    nextBall,
    stats,
    startGame,
    pauseGame,
    resumeGame,
    resetGame,
    handleCellClick
  } = useGameState();

  const renderGameControls = () => {
    switch (gameState) {
      case GameState.MENU:
        return (
          <div className="game-controls">
            <h1 className="game-title">Color Chain Reaction</h1>
            <p className="game-description">
              ÈªûÊìäÊ†ºÂ≠êÊîæÁΩÆÂΩ©Ëâ≤ÁêÉÔºåÁï∂Áõ∏ÂêåÈ°èËâ≤ÁöÑÁêÉÁõ∏ÈÑ∞3ÂÄã‰ª•‰∏äÊôÇÊúÉÁî¢ÁîüÈÄ£ÈéñÂèçÊáâÔºÅ
            </p>
            <button className="control-button start" onClick={startGame}>
              ÈñãÂßãÈÅäÊà≤
            </button>
          </div>
        );
      
      case GameState.PLAYING:
        return (
          <div className="game-controls">
            <button className="control-button pause" onClick={pauseGame}>
              Êö´ÂÅú
            </button>
            <button className="control-button reset" onClick={resetGame}>
              ÈáçÊñ∞ÈñãÂßã
            </button>
          </div>
        );
      
      case GameState.PAUSED:
        return (
          <div className="game-controls">
            <h2 className="pause-title">ÈÅäÊà≤Êö´ÂÅú</h2>
            <button className="control-button resume" onClick={resumeGame}>
              ÁπºÁ∫åÈÅäÊà≤
            </button>
            <button className="control-button reset" onClick={resetGame}>
              ÈáçÊñ∞ÈñãÂßã
            </button>
          </div>
        );
      
      case GameState.GAME_OVER:
        return (
          <div className="game-controls">
            <h2 className="game-over-title">ÈÅäÊà≤ÁµêÊùü</h2>
            <div className="final-stats">
              <p>ÊúÄÁµÇÂàÜÊï∏: <span className="highlight">{stats.score.toLocaleString()}</span></p>
              <p>ÈÅîÂà∞Á≠âÁ¥ö: <span className="highlight">{stats.level}</span></p>
              <p>ÈÅäÊà≤ÊôÇÈñì: <span className="highlight">{Math.floor(stats.timeElapsed / 60)}:{(stats.timeElapsed % 60).toString().padStart(2, '0')}</span></p>
            </div>
            <button className="control-button restart" onClick={startGame}>
              ÂÜçÁé©‰∏ÄÊ¨°
            </button>
            <button className="control-button menu" onClick={resetGame}>
              ÂõûÂà∞‰∏ªÈÅ∏ÂñÆ
            </button>
          </div>
        );
      
      default:
        return null;
    }
  };

  const renderGameInstructions = () => {
    if (gameState === GameState.MENU) {
      return (
        <div className="game-instructions">
          <h3>ÈÅäÊà≤Ë™™Êòé</h3>
          <ul>
            <li>üéØ ÈªûÊìä‰ªªÊÑèÂàóÊîæÁΩÆÂΩ©Ëâ≤ÁêÉ</li>
            <li>üåà Áõ∏ÂêåÈ°èËâ≤Áõ∏ÈÑ∞3ÂÄã‰ª•‰∏äÊúÉÊ∂àÈô§</li>
            <li>‚ö° ÈÄ£ÈéñÂèçÊáâÂèØÁç≤ÂæóÊõ¥È´òÂàÜÊï∏</li>
            <li>üéÅ ÁâπÊÆäÁêÉÔºöÂΩ©ËôπÁêÉÔºàËê¨ËÉΩÔºâ„ÄÅÁÇ∏ÂΩàÁêÉÔºàÁàÜÁÇ∏ÁØÑÂúçÔºâ</li>
            <li>‚è∞ ÁêÉÊúÉËá™ÂãïÊéâËêΩÔºåÂà•ËÆìÈ†ÇÈÉ®Â°´ÊªøÔºÅ</li>
            <li>üèÜ ÊåëÊà∞Êõ¥È´òÂàÜÊï∏ÂíåÁ≠âÁ¥ö</li>
          </ul>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="app">
      <div className="app-container">
        {gameState === GameState.MENU ? (
          <div className="menu-layout">
            {renderGameControls()}
            {renderGameInstructions()}
          </div>
        ) : (
          <div className="game-layout">
            <div className="game-area">
              <GameBoard 
                board={board}
                onCellClick={handleCellClick}
                nextBall={nextBall || undefined}
              />
            </div>
            
            <div className="sidebar">
              <GameStats stats={stats} />
              {renderGameControls()}
            </div>
          </div>
        )}
        
        {(gameState === GameState.PAUSED || gameState === GameState.GAME_OVER) && (
          <div className="overlay">
            <div className="overlay-content">
              {renderGameControls()}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
