import React, { useEffect, useRef } from 'react';
import { Difficulty } from '../types/levels';
import './WeatherEffects.css';

interface WeatherEffectsProps {
  currentLevel: Difficulty;
  isActive: boolean;
}

interface Particle {
  x: number;
  y: number;
  vx: number;
  vy: number;
  size: number;
  opacity: number;
  life: number;
  maxLife: number;
}

const WeatherEffects: React.FC<WeatherEffectsProps> = ({ currentLevel, isActive }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const particlesRef = useRef<Particle[]>([]);
  const animationRef = useRef<number>();

  const getWeatherType = () => {
    switch (currentLevel) {
      case Difficulty.EASY:
        return 'none';
      case Difficulty.MEDIUM:
        return 'sparkles';
      case Difficulty.HARD:
        return 'snow';
      case Difficulty.SUPER:
        return 'stars';
      case Difficulty.GOD:
        return 'energy';
      default:
        return 'none';
    }
  };

  const createParticles = (weatherType: string) => {
    const canvas = canvasRef.current;
    if (!canvas) return [];

    const particles: Particle[] = [];
    let count = 0;

    switch (weatherType) {
      case 'sparkles':
        count = 30;
        break;
      case 'snow':
        count = 50;
        break;
      case 'stars':
        count = 40;
        break;
      case 'energy':
        count = 60;
        break;
      default:
        return [];
    }

    for (let i = 0; i < count; i++) {
      particles.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        vx: (Math.random() - 0.5) * (weatherType === 'energy' ? 4 : 2),
        vy: weatherType === 'snow' ? Math.random() * 2 + 1 : (Math.random() - 0.5) * 2,
        size: Math.random() * (weatherType === 'stars' ? 3 : 2) + 1,
        opacity: Math.random() * 0.8 + 0.2,
        life: 0,
        maxLife: Math.random() * 200 + 100
      });
    }

    return particles;
  };

  const updateParticles = (particles: Particle[], weatherType: string) => {
    const canvas = canvasRef.current;
    if (!canvas) return particles;

    return particles.map(particle => {
      particle.life++;
      particle.x += particle.vx;
      particle.y += particle.vy;

      // 邊界處理
      if (particle.x < 0) particle.x = canvas.width;
      if (particle.x > canvas.width) particle.x = 0;
      if (particle.y < 0) particle.y = canvas.height;
      if (particle.y > canvas.height) particle.y = 0;

      // 特殊效果
      switch (weatherType) {
        case 'sparkles':
          particle.opacity = 0.5 + Math.sin(particle.life * 0.1) * 0.3;
          break;
        case 'energy':
          particle.vx += (Math.random() - 0.5) * 0.1;
          particle.vy += (Math.random() - 0.5) * 0.1;
          particle.opacity = 0.8 + Math.sin(particle.life * 0.05) * 0.2;
          break;
        case 'stars':
          particle.opacity = 0.3 + Math.sin(particle.life * 0.02) * 0.7;
          break;
      }

      // 重生粒子
      if (particle.life > particle.maxLife) {
        particle.life = 0;
        particle.x = Math.random() * canvas.width;
        particle.y = weatherType === 'snow' ? -10 : Math.random() * canvas.height;
        particle.opacity = Math.random() * 0.8 + 0.2;
      }

      return particle;
    });
  };

  const drawParticles = (ctx: CanvasRenderingContext2D, particles: Particle[], weatherType: string) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);

    particles.forEach(particle => {
      ctx.save();
      ctx.globalAlpha = particle.opacity;

      switch (weatherType) {
        case 'sparkles':
          // 閃爍星星
          ctx.fillStyle = '#ffd700';
          ctx.beginPath();
          for (let i = 0; i < 5; i++) {
            const angle = (i * Math.PI * 2) / 5;
            const x = particle.x + Math.cos(angle) * particle.size;
            const y = particle.y + Math.sin(angle) * particle.size;
            if (i === 0) ctx.moveTo(x, y);
            else ctx.lineTo(x, y);
          }
          ctx.closePath();
          ctx.fill();
          break;

        case 'snow':
          // 雪花
          ctx.fillStyle = '#ffffff';
          ctx.beginPath();
          ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
          ctx.fill();
          break;

        case 'stars':
          // 星星
          ctx.fillStyle = '#87ceeb';
          ctx.beginPath();
          ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
          ctx.fill();
          // 十字光芒
          ctx.strokeStyle = '#87ceeb';
          ctx.lineWidth = 1;
          ctx.beginPath();
          ctx.moveTo(particle.x - particle.size * 2, particle.y);
          ctx.lineTo(particle.x + particle.size * 2, particle.y);
          ctx.moveTo(particle.x, particle.y - particle.size * 2);
          ctx.lineTo(particle.x, particle.y + particle.size * 2);
          ctx.stroke();
          break;

        case 'energy':
          // 能量粒子
          const gradient = ctx.createRadialGradient(
            particle.x, particle.y, 0,
            particle.x, particle.y, particle.size * 2
          );
          gradient.addColorStop(0, '#00ffff');
          gradient.addColorStop(1, 'transparent');
          ctx.fillStyle = gradient;
          ctx.beginPath();
          ctx.arc(particle.x, particle.y, particle.size * 2, 0, Math.PI * 2);
          ctx.fill();
          break;
      }

      ctx.restore();
    });
  };

  const animate = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const weatherType = getWeatherType();
    if (weatherType === 'none') return;

    particlesRef.current = updateParticles(particlesRef.current, weatherType);
    drawParticles(ctx, particlesRef.current, weatherType);

    animationRef.current = requestAnimationFrame(animate);
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const updateCanvasSize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    updateCanvasSize();
    window.addEventListener('resize', updateCanvasSize);

    if (isActive) {
      const weatherType = getWeatherType();
      if (weatherType !== 'none') {
        particlesRef.current = createParticles(weatherType);
        animate();
      }
    }

    return () => {
      window.removeEventListener('resize', updateCanvasSize);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [currentLevel, isActive]);

  const weatherType = getWeatherType();
  if (!isActive || weatherType === 'none') return null;

  return (
    <canvas
      ref={canvasRef}
      className="weather-canvas"
    />
  );
};

export default WeatherEffects;
